const inputData = $input.all();
let highestConfidenceEmail = null;
let maxConfidence = -1;

// Process each input item
for (const item of inputData) {
    // Check if emails array exists and has items
    if (item.json.data && item.json.data.emails && item.json.data.emails.length > 0) {
        const emails = item.json.data.emails;

        // Find the email with highest confidence
        for (const email of emails) {
            if (email.confidence > maxConfidence) {
                maxConfidence = email.confidence;
                highestConfidenceEmail = {
                    value: email.value,
                    confidence: email.confidence
                };
            }
        }
    }
}

// Return the highest confidence email or "Not Found" if none exists
return [{
    json: highestConfidenceEmail || {
        value: "Not Found",
        confidence: 0
    }
}];

